import React from "react";
// import logo from './logo.svg';
import "./App.css";
import { Weather } from "./Components/Weather";
import Time from "./Components/Time";
import Name from "./Components/Name";
//bg

function App() {
  let morningImage = "./assets/img/morning.jpg";
  let topStyle = {
    backgroundImage: `url(${morningImage})`,
    width: "100%",
    height: "100vh",
    opacity: 0.5,
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    backgroundSize: "cover"
  };
  let timezone = new Date().toLocaleTimeString();
  let pagi = "AM";
  let malam = "PM";
  console.log(timezone.includes(pagi));
  console.log(morningImage);

  if (timezone.includes(pagi) === true) {
    return (
      <div className="body" style={{ topStyle }}>
        <div className="App">
          <Name />
          <Time />
          <Weather />
        </div>
      </div>
    );
  } else if (timezone.includes(malam) === true) {
    return (
      <div className="body">
        <div className="App">
          <Name />
          <Time />
          <Weather />
        </div>
      </div>
    );
  } else {
    return null;
  }
}

export default App;
