import React from "react";
import ReactWeather from "react-open-weather";
import "react-open-weather/lib/css/ReactWeather.css";
import axios from "axios";
export default class Weather extends React.Component {
  constructor() {
    super();
    this.state = {
      data: "",
      loading: true,
      API: process.env.REACT_APP_PUB_KEY,
      lat: null,
      lon: null
    };
  }
  componentDidMount() {
    // this.getIP();
    this.getLocation();
  }
  getLocation = async () => {
    let result = await axios.get(
      `http://api.ipstack.com/check?access_key=6bec36e242f15bca37761f998bd88d62`
    );
    let city = result.data.city;
    // let resJson = JSON.stringify(result);
    // console.log(city);

    this.setState({
      ...this.state,
      city: city
    });
  };

  // getIP = () => {
  //   axios
  //     .get(
  //       `https://api.openweathermap.org/data/2.5/weather?q=Depok &appid=${this.state.API}`
  //     )
  //     .then(res => console.log(res));
  // };

  render() {
    return (
      <ReactWeather
        forecast="today"
        unit="metric"
        apikey={this.state.API}
        type="geo"
        lat=""
        lon=""
      />
    );
  }
}
